text = text.lower()

def hist(text,char):                    #funkcja która zlicza ilość wystąpień litery w tekscie
    count = 0
    for letter in text:
        if char == letter:
            count += 1
    return count


letters = ['a', 'b', 'c', 'd', 'e', 'f', 'g', 'h', 'i', 'j', 'k', 'l', 'm', 'n', 'o', 'p', 'r', 's', 't', 'u', 'q', 'w', 'v', 'x', 'y', 'z', 'ę', 'ó', 'ą', 'ś', 'ł', 'ż', 'ź', 'ć']
